const executor = actor.id;
const targets = actor.getOtherRegistry();

// compute player with max atk,
let target = false;
let maxAtk = 0;
targets.forEach(actor => {
	let actorAtk = actor.getAttack();
	if (actorAtk > maxAtk) {
		maxAtk = actorAtk;
		target = actor.id;
	}
});

target = target || targets.random().id;

console.log("chose", target, "with atk:", maxAtk)

actor.executingAction = scene.stores.Actions.create("Dissing", {executor, target});
callback();
