properties.action:
// PARAMETER SANITIZATION
const {actor, menu} = options;
const {Menus, Enemies, Players} = scene;

// COMPUTE TARGETS
const targets = [];
Enemies.forEach(a => targets.push(a.id));
Players.forEach(a => targets.push(a.id));

// COMPUTE COMMAND OPTIONS
const actionOptions = {dieRoll: actor.getAttack()};

// console debug
const executor = actor.id;
const targetMenu = Menus.build("Targets", targets, executor, "SimpleAttack", actionOptions);
Menus.load(targetMenu);
Menus.selectedMenu.init(() => {});
